import { useState } from 'react';

const Square = ({ value, onClick }) => (
  <button className="square" onClick={onClick}>
    {value}
  </button>
);

const Board = ({ isXNext, board, onMove }) => {
  const handleSquareClick = (index) => {
    if (getWinner(board) || board[index]) return;

    const updatedBoard = board.slice();
    updatedBoard[index] = isXNext ? 'X' : 'O';
    onMove(updatedBoard);
  };

  const winner = getWinner(board);
  const status = winner
    ? `Winner: ${winner}`
    : `Next player: ${isXNext ? 'X' : 'O'}`;

  return (
    <>
      <div className="status">{status}</div>
      {[0, 3, 6].map((start) => (
        <div className="board-row" key={start}>
          {[0, 1, 2].map((offset) => {
            const idx = start + offset;
            return (
              <Square
                key={idx}
                value={board[idx]}
                onClick={() => handleSquareClick(idx)}
              />
            );
          })}
        </div>
      ))}
    </>
  );
};

const Game = () => {
  const [history, setHistory] = useState([Array(9).fill(null)]);
  const [currentStep, setCurrentStep] = useState(0);

  const isXNext = currentStep % 2 === 0;
  const currentBoard = history[currentStep];

  const handleMove = (nextBoard) => {
    const updatedHistory = [...history.slice(0, currentStep + 1), nextBoard];
    setHistory(updatedHistory);
    setCurrentStep(updatedHistory.length - 1);
  };

  const jumpToStep = (step) => {
    setCurrentStep(step);
  };

  const moves = history.map((_, move) => (
    <li key={move}>
      <button onClick={() => jumpToStep(move)}>
        {move === 0 ? 'Go to game start' : `Go to move #${move}`}
      </button>
    </li>
  ));

  return (
    <div className="game">
      <div className="game-board">
        <Board isXNext={isXNext} board={currentBoard} onMove={handleMove} />
      </div>
      <div className="game-info">
        <ol>{moves}</ol>
      </div>
    </div>
  );
};

const getWinner = (board) => {
  const lines = [
    [0, 1, 2], [3, 4, 5], [6, 7, 8], // rows
    [0, 3, 6], [1, 4, 7], [2, 5, 8], // cols
    [0, 4, 8], [2, 4, 6]             // diags
  ];

  for (let [a, b, c] of lines) {
    if (board[a] && board[a] === board[b] && board[a] === board[c]) {
      return board[a];
    }
  }
  return null;
};

export default Game;
